let catalogRow=document.querySelector(".catalog-row");const filter=document.querySelector(".filter__select"),products=document.querySelectorAll(".catalog-column");function sort(e){e=e.target.value;if("default"===e)pushChild(catalogRow,products);else{let t=[];var r;[...products].map(e=>{t.push(e)});"price"===e?(r=t.sort((e,t)=>{return e.querySelector(".catalog-price").textContent.replace(/\s/g,"")-t.querySelector(".catalog-price").textContent.replace(/\s/g,"")}),pushChild(catalogRow,r)):"rating"===e&&(r=t.sort((e,t)=>{return e.querySelector(".catalog-rating-item").textContent-t.querySelector(".catalog-rating-item").textContent}),pushChild(catalogRow,r))}}function removeChild(e){for(;e.firstChild;)e.removeChild(e.firstChild)}function pushChild(t,r){removeChild(t);for(let e=0;e<r.length;e++)t.appendChild(r[e])}filter.addEventListener("change",sort);const prices=document.querySelectorAll(".catalog-price");prices.forEach(e=>{let t=+e.innerText;var r=t.toLocaleString("ru-RU");e.innerText=r});